INSERT INTO `mbti_analysis` VALUES (1, 'ESTJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">理智、善分析、果断、意志坚定，以系统化的方式组织具体事实。喜欢事先组织细节和操作程序与他人一起完成任务</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">事先察觉、指出、修正不足之处 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以逻辑的、客观的方式评论规划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">组织规划、生产、人力要素，实现组织目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">监督工作以确保任务正确完成 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以逐步进行的方式坚持到底 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">直接领导，快速管理 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">运用过去经验解决问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">直接、明确地识别问题的核心 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">决策和执行决策非常迅速 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">传统型领导，尊重组织内部的等级和组织获得的成就 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">积极主动型，学习间接经验，采用结构化的学习方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">实际型，关注他们能运用的学习内容 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）情感 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">问题解决模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢根据相关的事实和细节进行逻辑分析，从而控制情境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为达到理想结果，会考虑更广阔的前景以及对人们和自己的影响 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与努力工作、有坚定决心把工作做好的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">任务型定向的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有组织和组织结构的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有团队计划的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供稳定性和预测性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">致力于绩效和生产性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励完成目标的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">决策太迅速，也给他人施以同样的压力 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">不能察觉变革的需要，因为相信一切都在正常运作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在完成任务过程中，忽视人际间的小细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">长期忽视自己的感受和准则，可能会被自己的情感击跨 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">决策之前需考虑各种因素，包括人的因素 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要促使自己看到他人要求变革而获得的利益 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需做特别的努力学会赞赏别人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需从工作中抽点时间考虑和识别自己的情感和价值观&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (2, 'ESTP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">行为定向型，讲究实效、足智多谋、注重现实，以最有效的途径解决问题。喜欢事件即时发生，然后在复杂的情境中找到解决问题的方法。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">采用协商的方式使任务顺利完成 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">保持组织运作的活跃状态，促使变化发生 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">运用直接和现实的工作方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">评估风险 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">注意和记忆事实信息 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对危机中的管理有充分准备 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">说服他人接受自己的观点 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">直率、自信的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">按最有利的路径进行组织工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">重视行动和即时结果 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">主动型、间接经验型、尝试错误型的学习方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">实际型，注意力集中在即刻能应用的学习内容 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）直觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">问题解决模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢现实、具体地评估环境，然后用逻辑分析以后采取的步骤 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，会考虑对人们的影响，寻找其它可选择的可能性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与活泼、结果定向型、重视直接经验的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有规则，但承认差异性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中允许有开玩笑的时间 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能提供工作灵活性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">技术型定向的环境，有最新的设备 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">身体感到舒适的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对即刻的需求能做出反应的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">当快速行为时，显得苛求、强硬、感觉迟钝 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过分集中于即时行为，从而失去行为的更广阔、深远的意义 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">转移到下一个待解决的问题而不能坚持到底解决目前的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">会被工作以外的活动吸引，如体育运动和其它娱乐活动。 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需抑制自己的任务型定向，分析他人的情绪感受 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需在快速决定之前，事先计划，考虑更多的因素 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需完成眼前的任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需以适当的观点看待工作和娱乐&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (3, 'ESFJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">乐于助人，机智，富有同情心，注重秩序，把与他人相处和谐看得很重要，喜欢组织人们和制定计划完成眼前的任务。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">服务型定向 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">密切关注组织中每个人的需要，并使他们满意 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以及时、精确的工作方式完成任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">尊重规则和权威 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有效处理日常管理任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关心他人的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以良好的人际关系赢得合作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">让人们活跃起来 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">承担繁重的工作，坚持到底 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">发扬组织的传统精神 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">系统性、参与性、个体性的学习方式，用较多时间讨论新知识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">学习早已有实践应用的应用性材料 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）思维 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢考虑准则以及对人们的影响，也关注相关的事实和有用的细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获取理想结果，需识别其它人际关系，然后理智、冷静地分析 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与诚恳、具有合作性、乐于帮助他人的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">目标定向型的工作环境，提供有益的、合适的工作程序 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励组织行为和个体工作绩效的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励人际间的友谊的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">赞赏他人和开朗的工作氛围 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">培养人际间关心和敏感察觉他人需要的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注事实和价值观的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">避免和回避冲突 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因致力于令他人满意而忽略自己 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供自己认为是对组织和对他人最好的建议 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">不经常有时间客观地反思过去、展想未来 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需学会注意差异性和处理冲突 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需学会分离出自己的需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需学会更客观地听取真正需要什么 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">做决策时，需考虑决策的理性、全局性的意义&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (4, 'ESFP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">友好、开朗，爱开玩笑，活泼，天性喜欢与他人相处。喜欢与其他活泼、快节奏的人一起工作，同时也会根据判断做出不同选择。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织创造具有活力、热情、合作的氛围 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织提供积极发展的规划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">具有行动力，营造热情、轻松的气氛 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">协调人、信息、资源的关系 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以接纳和尊重他人的方式与人相处，温和宽容地对待他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">促进善意和合作的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢从开头管理某个工程 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">消除紧张气氛，把人们带入轻松的情境里 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注解决即时出现的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">促进人际间有效的交流 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">利用充裕的时间通过讨论获取新知识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">学习事实性的知识，搞清楚这些知识如何发挥作用的 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）直觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢对情境进行现实和具体的评估，尤其是对于人更是如此 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最佳结果，需增强客观性，从长远的眼光看待不同事物 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与有活力的、轻松愉快、关注现实的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">活跃、行为定向的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">培养快节奏做事的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有适应性强、喜爱自由的人的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">强调和谐、友好、赞赏别人的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">乐观的、注重交往的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有吸引力、丰富多彩的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺陷</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为保持和谐，过度强调主观性论据 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">行动前不太考虑眼前的事实 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能花太多的时间在社会关系上而忽视任务本身 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">常常有始无终 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为减少非个体性冲突，做决策时需理智分析决策的意义 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">进行管理工作前应事先制定计划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需平衡花费在任务和社会性交往上的时间 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需致力于完成计划，对时间进行管理&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (5, 'ENTJ', '<p>\r\n    <br/>\r\n</p>\r\n<p style=\"text-indent: 28px;\">\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">具有逻辑性、组织性、客观性、果断性。喜欢与他人一起工作，尤其从事管理工作和制定战略计划时。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">制定经过深思熟虑的组织计划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织建立组织结构 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">制定有远大目标的战略规划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">快速管理，迅速解决需要解决的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">即刻处理因混乱和效率低引起的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">富于活力的行为定向型领导模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织制定长远规划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">必要时，采用直接的、强硬的管理方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢复杂问题并足智多谋地解决这些问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">尽可能多地参与组织管理 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢由专家传授知识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对挑战和问题抱开放态度 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）情感 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">根据内在的理解进行逻辑分析从而控制局面 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，对事实资料进行现实性决策，同时考虑决策对人们和自己的影响 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与结果定向型、独立的、有能力的、注重解决复杂问题的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">目标型定向的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有效率的组织系统和员工 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作环境中即刻奖励做出挑战努力的员工 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励果断的人的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中有意志坚定的人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">组织关注主要的组织计划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注任务而忽视人们的需要和对组织的贡献 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">忽略现实的考虑和对现实局限性的认识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">决策太迅速，表现得缺乏耐心，盛气凌人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">忽视和抑制自己和他人的情感 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要考虑人的因素，赞赏他人对组织的贡献 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">行动前先检查现实的、人力的、环境的资源是否可获得 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">决策前花些时间考虑和反思各个方面的因素 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要学会鉴别和重视自己和他人的情感</span></span>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (6, 'ENTP', '<p>\r\n    <br/>\r\n</p>\r\n<p style=\"text-indent: 28px;\">\r\n<span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">富于创新，具有战略眼光，多才多艺，分析型思维，具有创业能力。喜欢与他人一起从事需要非凡智慧的创始性活动。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">把限制看作是挑战加以排除 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供完成任务的新方法 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">把问题放在理论框架中进行考虑 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提倡创新，激励他人创新 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢接受适应未来发展需要的复杂的挑战 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">制定理论体系满足组织的需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励他人的独立性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">运用逻辑分析寻找变革的模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对于自己想做的事使用强制性的理由 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在人与组织之间扮演促进者的角色 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">主动、概念型的学习，喜欢由专家传授知识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">接受挑战型，定向于未来的发展蓝图 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）感觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢探索未来的前景和发展模式，理智地分析每一个正向和反向的结果 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最理想结果，关注人们的需要和相关的事实和细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与独立的、按理论模型解决复杂问题的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供灵活性和挑战性的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">变革型和非官僚作风的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">拥有具有竞争力的个体的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励挑战风险行为的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励行为自主性和自由性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注未来发展远景的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过多依赖模型而忘记现实状况 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因竞争心而不会赞赏他人的付出 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因过分扩展自己而筋疲力尽 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能抵制正规的程序和准则 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要注意各个方面的因素和基本的事实 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要承认他人贡献的有效性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要设立现实性的开始与结束的期限，知道何时该结束 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要学会怎样在组织里如何工作&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>	0				\r\n', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (7, 'ENFJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">关注人际关系，理解、宽容和赞赏他人，是良好沟通的促进者。喜欢与他人一起工作，致力于完成与人们的发展有关的各种任务。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有强烈的关于组织该如何对待人们的观念 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢领导和促进团队的建立 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励合作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">传播组织的价值观和准则 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">致力于获得丰硕的组织成果 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">富于热情和赞扬他人的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以参与的态度管理员工和工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">满足员工的需要，努力使每个员工满意 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">促使组织的行为与组织的价值观一致 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励施行给人们带来利益的变革 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在相互交流和合作中学习重要的内容 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有良好结构的学习材料，在学习中能获得更多鼓励 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）思维 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">先判断发展计划是否考虑能取得的绩效和对人们的影响 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最佳结果注意更多事实资料，然后进行理智、冷静地分析 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">喜欢的工作环境</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与那些关注变革并通过变革改变人们的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢人际定向型和社会型的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励支持和称赞他人的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">富有同情精神和和睦气氛的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励自我表现的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">稳定而注重果断性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">注重反馈和秩序的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能会理想化他人，因而遭受他人表面忠诚的蒙蔽 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能回避有冲突的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因重视人际关系而忽视任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过度自我批评 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要认识人们的局限性，捍卫真正的忠诚 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要学会建设性地处理冲突 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要学会同时关注任务中的细节问题和完成任务的人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要认真听取客观的评价，少一些自我批评&nbsp;</span></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (8, 'ENFP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">热情，富有洞察力和创新性，多才多艺，不知疲倦地寻求新的希望和前景。喜欢在团队中工作，致力于从事能给人们带来更好的改变的事情。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能察觉改革的需要并发起变革 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注前景的发展，尤其是人们的未来发展 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以富有感染力的热情激励和说服他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">把创造性和想象性体现在制定的计划和贯彻的行为中 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">欣赏和认同他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">富有活力、热情的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢进行首创性管理 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">经常是重要事件的发言人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作中提倡和支持人们的自主性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注如何激励他人，如何鼓励他人付诸于行动 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">积极主动型、经验型、想象型 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对学习内容感兴趣，不管它们是否有实际应用性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）感觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢根据自己的价值观和准则探索创造性发展的各种可能性和前景 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最佳结果，冷静理智分析，考虑相关的事实资料和各种细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与想象力丰富、致力于人们未来发展的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">允许表现交际能力和智力才能的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">爱好参与的氛围，与不同的人分享不同的观点 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供变化和具有挑战性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励提出观点和想法的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有弹性、自由度大、少限制的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">气氛愉悦和随意的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在没完成已经提出的计划之前又转移到新的想法和计划上 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">忽视相关的细节和事实资料 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过分扩展，尝试做的事情太多 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因寻求可能的最佳结果而拖延工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要根据重要性事先做好安排，先做最重要的，然后坚持到底。 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要关注重要的细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要学会筛选任务，不要试图去做所有具有吸引力的任务。 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为达成目标，需使用制定计划和进行时间管理的技巧&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (9, 'ISTJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">详尽、精确、系统、勤劳，关注细节。致力于改善组织程序与过程，无论组织处在发展的顺境还是逆境，都对组织保持忠诚。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">平稳地、按计划完成组织任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">重视细节并慎重处理 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">做事力尽完美 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">重信用，并具有坚持性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在组织里工作倍感舒适 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以事实和经验做决定 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">建立可靠、稳定、持续的工作绩效 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">尊重传统和等级制度 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励遵循规则完成任务的员工 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注组织的即时性和实际性需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">具体、有序的学习方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对目前有实际应用的学习模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）直觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢完全依据事实在逻辑框架里进行分析 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，需考虑对人们的影响，然后寻找更多的可能性和其它含义 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与现实、工作努力、关注事实和结果的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能长期提供安全性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励稳步发展和按期完成任务的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">使用系统性工作方法的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">任务型定向和鼓励坚定意志的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供安静、整齐设施的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中允许有不被打扰工作的个人空间 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因受惠于日常工作而忽视具有长远意义的目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能忽视人际交往的细节 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作方法刻板、不灵活，对变革较少开放态度 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">期望他人和自己一样，同样注意细节和服从管理程序 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">除了关注现实问题，需关注更深远的、定向于未来的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需考虑人的因素，向他人表达其应得的赞赏 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为避免陈规，尝试寻找新的选择 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需培养耐心，应付那些需要用不同方式沟通或忽视规则和程序的人&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (10, 'ISTP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">注重实用性，尊重事实，寻求有利方法，具有现实性，只信服被论证的结果。喜欢独立工作，依靠逻辑和足智多谋解决即时出现的组织问题。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在需要的场合，是解决麻烦问题的能手 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在感兴趣的领域里，发挥行走的信息库的作用 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">计算出克服障碍、完成任务的最实际的途径 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在危机中保持镇定，发挥安抚他人情绪的作用 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在规划中增加其专业领域里的知识和技能 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以身作则 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">一视同仁，尊重每个人的价值 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">面临麻烦时，采用最有利的方法做出快速反应 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">宽松管理员工，喜欢最少监督自己 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">采用明晰、理性的管理原则 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">生动性、娱乐性的学习模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对有用的内容和有实际应用的学习内容感兴趣 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）情感 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">问题解决模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢依据具体事实以自身具有的内部逻辑构建问题和解决问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，需要考虑其它可能性和对人们的影响 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与行为定向、关注即时情境的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">计划定向和任务定向的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">重视理性分析的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励对问题做出快速反应的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">允许间接经验的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供合适的工作自由度的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">培养独立性和自主性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">只关注对自身重要的事而对其它事漠不关心 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在先前的努力获得成果前，缺少坚持性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">努力不足，过度注重有利性而走捷径 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">犹豫不决，欠缺兴趣、活力、坚持性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要增强开放性，关心他人，与他人共享信息 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需发展坚持性，改变沟通模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">加强计划性，付出更多努力获取想要的成功 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需发展设置和保持目标的方法&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (11, 'ISFJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">仁慈、忠诚、体谅他人、善良，不怕麻烦帮助需要帮助的人。喜欢充当后盾，提供支持和鼓励。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">考虑组织中每个人的实际需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">采用坚持到底的工作方式实现组织的目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对细节和日常惯例非常耐心，甚至不辞辛苦，责任感强 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">付出努力自愿为他人提供服务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">做事力求完美 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">开始可能不愿担任领导，但当需要承担领导任务时，会接受 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">希望自己和他人服从组织的需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以个人的影响力作为后盾 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">认真遵守传统程序和规则 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">观察细节以获得现实的结果 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">花费充足的时间以结构化的方式，安静地记忆材料 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">注重实用性，关注做什么可以为人们提供帮助 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）直觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢完全依据事实，尤其是当应用于人和准则方面时 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，需退一步思考问题的逻辑，然后寻找更多的可能性和其它含义 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与认真、从事组织性任务的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能提供安全性和预测性的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">组织结构明晰的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能保持安静、有一些个人空间的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">做事坚持到底的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">充满个人化、友好、体谅氛围的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">服务型定向的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过于谨慎小心，尤其是对待未来发展 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">向他人表明自己观点时，显得意志不太坚定 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因安静、忘却自我的特性而低估自己 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过度依赖自己的经验，不能根据环境和其它需要灵活调整 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作中需要估计风险，以积极、全面的观点看待未来 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需发展更多的自信和直率 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">学会宣扬自己的成就 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对其它形式的做事方式需保持开放态度&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (12, 'ISFP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">温和、体贴、灵活、具有开放性。富有同情心，尤其对那些需要帮助的人。喜欢在合作和充满和谐气氛的环境中工作，但常常是在完成他们自己任务的时候。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对组织中每个人的需要都做出反应 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以实际行动保证他人获得福利 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">对自己的工作投进特别的热情和愉悦 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因具有合作的天性，把人与任务很好地匹配起来 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注怎样对待员工 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">不喜欢担任领导，喜欢在团队中担任协调者的角色 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">用自己个人的忠诚激发他人工作的积极性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">常常更多地采用表扬和支持的方式，较少批评他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">随环境所需而做调适 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">通过了解他人良好的意图温和地说服他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">安静地学习直接经验 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">学习实际的、能帮助他人的内容 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）思维 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">问题解决模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢从实用的角度考虑对自己和他人真正重要的事物 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得理想结果，需考虑其他人际关系和其它可能性，然后更客观地决定事情 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与合作的、安静地享受工作愉悦感的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">允许有个人空间的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">与致力于和谐相处的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能提供灵活性和安全感的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有艺术感染力的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">讲究礼貌的同事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">追求实际效果的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能太信任他人，不愿持怀疑态度 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为避免冲突而不批评他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">只关注眼前的损失 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过度自我批评，容易受伤害 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需发展以怀疑的态度分析他人提供的信息 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需学会给他人负面反馈，处理好冲突 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需发展更广阔、更朝向未来定向的观念 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需对他人更果断，对自己有更多赞赏。&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (13, 'INTJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">独立而极具个性化，具有专一性和果断性，相信自己的眼光，漠视众人的怀疑。喜欢独自完成复杂的工程。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织提供理论观点和设计技术 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">把想法变成行动计划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为达成目标排除障碍 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有强烈的关于组织应该成为怎样的组织的理念 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">促使组织中的每个人明白组织是由许多复杂、相互作用的部分组成的整体系统 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">促使自己和他人完成组织目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">坚定有力地贯彻执行组织的理念 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">要求自己和他人具有顽强意志 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">构思、创造新的模型 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">必要时，意志坚定地重建整个组织系统 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">个性化、思考式学习方式，深入其感兴趣的领域 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">智慧型、理论型学习方式，首先要提供一个宏伟蓝图 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）感觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢以其内在的认识制定战略、系统和结构，然后客观地做出决定 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最佳结果，会接纳他人和那些使自己的认识更加接近现实的细节资料 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢工作中有果断、理智接受挑战、致力于完成远期理念的人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作环境允许思考的独立性和个体性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">强调效率的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境里有具有竞争力和创造性的人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励和支持自主性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供创造机会的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">任务定向型和重视详尽思考的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能显得强硬，他人不敢接近 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">长时间不告诉他人自己的想法，因为认为他人也和自己一样认同自己的想法 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能很难实际操作理想化的想法 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过度关注任务而忽视他人的贡献 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">自己的个性化方式和想法可以征求他人的反馈和建议 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">与参与任务的人早一些沟通和讨论自己的想法和战略计划 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">当事实资料不支持自己的想法时，应面对现实 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">明确他人的贡献应受到鼓励和承认的&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (14, 'INTP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">讲究合理性，喜欢理论和抽象的事物，好奇心重，更喜欢构建思想，不太关注环境和人。喜欢单独工作，强调对自己的观点和方法拥有最大的自主权。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织设计理性、复杂的系统 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在处理错综复杂的问题中显示出其专业性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">同时拥有理智的短期和长期目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供理智的、分析的、批评的思维方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">关注核心问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在概念上分析问题和目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供逻辑思维模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">追求自主性的同时，也关注他人独立的领导模式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">依据他人的专业知识而非职位与其交往 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">追求与他人智慧上的交流而非情感交流 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">个体化学习方式，不设置开始与结束，只根据自己感兴趣的深度进行学习 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">广泛的、概念性的、能挑战智慧的学习方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）思维，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）情感 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在寻求各种可能的选择时，喜欢以自身内部的逻辑建构问题和解决问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获取最佳结果，需要同时关注现实状况和他人的需求 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢与独立的思考者、关注解决复杂问题的人共事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中允许个体有充足的时间和空间进行思考 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能培养思维独立性和创造性的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">能提供灵活的政策和程序的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">安静、尽可能少地开会的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">非结构化和非官僚作风的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励自我决定的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过于抽象，因而坚持下去不太符合现实需要 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过于理性化，解释起来太理论化 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">过多注意团队中一些小的不一致的地方 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">可能以批评式分析的方式对待人们，行动不考虑个体感受 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要关注现实中的细节，确立完成任务的具体步骤 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要简单地陈述事实 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得他人的合作，需要放弃细小的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要更好地认识他人，更多地表达对他人的赞赏&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (15, 'INFJ', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">相信自己的眼光，具有同情心和洞察力，温和地运用影响力。喜欢独立工作或与那些热衷于关注人们的成长与发展问题的小群体共同工作。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供服务于人类需要的远见卓识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">恪守职责 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">完善、始终如一地工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">找个感到静谧的时间段以集中精力提出具有创造性的观点 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在人与工作间建立复杂的相互作用关系 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以使个体和组织获得最大利益的远见从事领导工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">从合作中获益而非需要合作的形式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">采取平稳、认真、持续性的行为过程实现战略目标 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">通过工作实现自己的理想和抱负 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">意志坚定地激发他人实现他们的理想 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">极具个体化和思考式的学习方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">强调复杂、结构性的概念和关系的学习 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）思考，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）感觉 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢识别自己内在观点的可能性，尤其是与人和社会准则有关的问题 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为成功实现目标，对定向未来的远见卓识的客观性和现实的细枝末节的问题同样重视 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">工作中有致力于把为人类创造未来作为理想的人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有表现创造性和展示自我价值机会的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中鼓励营造和谐气氛和体谅他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有尊重他人需要的顺畅的管理机制 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">奖励个体的远见卓识 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">提供进行安静思考的时间和空间的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有组织有计划的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">发现自己的远见被忽视和低估 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">面对批评不太坦率 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因不太愿强迫别人而过度保守 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">仅从单一维度考虑他们认为对将来最有益的事 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">发展建议</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">在提出自己的观点时，需发展政治领悟力和自主性的技策 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需学会及时给他人建设性的反馈 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要不断征求他人的建议和获得他人反馈 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">需要以更放松和开放的态度面对现状&nbsp;</span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', '0', NULL, NULL, NULL, NULL);
INSERT INTO `mbti_analysis` VALUES (16, 'INFP', '<p>\r\n    <span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">具有开放性，是理想主义者，具有洞察力，灵活。希望自己的工作被认为是重要的。喜欢独立工作或在能发挥创造性的小团体里工作。</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">对组织的贡献</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">以自己的理想与他人沟通和说服他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">用组织共同的目标把人们团结起来 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">致力于为组织中的人们寻求匹配的岗位 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为组织提供新的理念和各种发展的可能性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">平稳地推进组织发展，建立组织的价值观 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">领导模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">采用便利的领导方式 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">倾向发挥独特的领导能力而不是担当传统性的领导角色 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">独立工作 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">更可能表扬而不是批评他人 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">鼓励员工以行动实现理想 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">学习模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">安静地专注于自己的兴趣，展示出丰富的想象力 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">灵活，专注自己和他人的发展 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">倾向性顺序</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">（</span>1<span style=\"font-family:宋体\">）情感，（</span><span style=\"font-family:Arial\">2</span><span style=\"font-family:宋体\">）直觉，（</span><span style=\"font-family:Arial\">3</span><span style=\"font-family:宋体\">）感觉，（</span><span style=\"font-family:Arial\">4</span><span style=\"font-family:宋体\">）思考 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">解决问题模式</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">思考真正对他人和自己重要的问题，找出具有创造性的可能性 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">为获得最佳结果，注意搜集事实资料以客观地做出决策 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">工作环境倾向性</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">喜欢处在有重要价值观、令人愉悦、效忠组织的人们中 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">处在合作、轻松氛围的环境里 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">允许个体性，也提供共同参与的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">环境中提供灵活、有弹性的工作程序，最少的常规 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">非官僚主义的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">安静的环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">有时间和空间进行思考的工作环境 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><span style=\"font-family:宋体\">潜在的缺点</span> </span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">因完美倾向而延误完成任务 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">一次行为想令太多人满意 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">没有调整理想适合客观现实 </span></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\"><br/></span><span style=\";font-family:Arial;color:rgb(17,17,17);font-size:13px\">Ø <span style=\"font-family:宋体\">思考