INSERT INTO `mbti_anwser` VALUES (191, 'B', '你努力去想其他的事情，使自己专注于别的活动。', 95, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (192, 'C', '你退避一旁，好让自己能够放松并思考。', 95, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (193, 'A', '我不能相信任何人。', 96, 'A', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (194, 'B', '我自己有什么事情不对。', 96, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (195, 'C', '大家都是一群笨蛋。', 96, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (196, 'A', '“我想让人们爱我。”', 97, 'A', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (197, 'B', '“别人爱不爱我无所谓，我只希望人们尊重我。”', 97, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (198, 'C', '“我不仅希望人们爱我，还希望他们对我推崇有加。”', 97, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (199, 'A', '你问自己：“我到底做错了什么，他为什么怨恨我？”', 98, 'A', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (200, 'B', '你对他很恼火：“他至少该回个短信！”', 98, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (201, 'C', '你感到绝望：“归根结底，我们不能相信任何人”', 98, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (202, 'A', '对让你处于这一境地的人心怀怨恨。', 99, 'A', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (203, 'B', '你对自己发泄情绪的对象感到愧疚。', 99, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (204, 'C', '你害怕自己的行为无法弥补，觉得关系已经被破坏。', 99, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (205, 'A', '好吧，我只好换个地方，离他们远一点。', 100, 'A', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (206, 'B', '我要问问他们，是想骑到我的头上吗？', 100, 'B', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
INSERT INTO `mbti_anwser` VALUES (207, 'C', '我会装疯卖傻让他们赶紧逃走，比如大声辱骂自己的脚趾头、用嘴发出奇怪的声音……', 100, 'C', 'N', 'admin', '2019-09-04 13:43:07', 'admin', '2019-09-07 15:11:44');
